clc;close all;clear all;Ix=imread('./images/test3.png');figure(1);imshow(Ix);I1 = double(Ix);I = double(Ix);r = double(Ix(:,:,1));g = double(Ix(:,:,2));b = double(Ix(:,:,3));#first functionIr = r.* (20/ 61);Ig = g.*(40/ 61);Ib = b ./ 61;Ir = double(Ir);Ig = double(Ig);Ib = double(Ib);amax = 1;amin = .1;fg = double(sizeof(Ig));fr = double(sizeof(Ir));fb = double(sizeof(Ib));I(:,:,1) = Ir;I(:,:,2) = Ig;I(:,:,3) = Ib;#second functionw1 = 0.3;w2 = 0.3;w3 = 0.4;for i = 1:1:6      #first function    am = amin + (amax-amin)*[(i-1)/5];    am        fg = Ig + (Ig.*max(max(g))-Ig.^2)./sqrt(Ig.^2 + am^2);    fb = Ib + (Ib.*max(max(b))-Ib.^2)./sqrt(Ib.^2 + am^2);    fr = Ir + (Ir.*max(max(r))-Ir.^2)./sqrt(Ir.^2 + am^2);            I1(:,:,1) = fr;    I1(:,:,2) = fg;    I1(:,:,3) = fb;        figure(i+1);    new_img = uint8(I1);    image(I);        #second function    Iy = rgb2ycbcr(I);        g = exp(-abs(I-Iy)./2*sigma^2)./2*pi*sigma;                 end