clc;close all;clear all;Io=imread('./images/test3.png');I = Io;r = Io(:,:,1);g = Io(:,:,2);b = Io(:,:,3);Ir = r.* (20/ 61);Ig = g.*(40/ 61);Ib = b ./ 61;amax = 1;amin = .1;fg = sizeof(Ig);fr = sizeof(Ir);fb = sizeof(Ib);figure(1);imshow(Io);for i = 1:1:6    am = amin + (amax-amin)*[(i-1)/5];    am    fg = Ig + (Ig.*(max(g)-Ig))./sqrt(Ig.^2 + am^2);    fb = Ib + (Ib.*(max(b)-Ib))./sqrt(Ib.^2 + am^2);    fr = Ir + (Ir.*(max(r)-Ir))./sqrt(Ir.^2 + am^2);    figure(i+1);    new_img = uint8(I);    image(I);    end